#include&lt;stdio.h&gt; #define N 20 int main() { char a[N]=&#34;abcb&#34;; char b[N]=&#34;0000&#34;; int i,j,m; for(i=0;i&lt;strlen(a)/2;i++) { for(j=i+1;j&lt;strlen(a);j++) { if(a[i]==a[j]) { printf(&#34;%d %d %d &#34;,i,j ,m); for(m=i;m&lt;=j;m++) { if(a[m]!=a[j+i-m]) break; b[m-i]=a[m]; b[j-i-m]=a[j+i-m]; if((m==j+i-m)||(m==j+i-m-1)) i=j; printf(&#34;%s\n&#34;,b); } } } } printf(&#34;%s\n&#34;,b); return 0; }

